package PersonalPracticeAssignment2;

public class AddEvenFor {

	public static void main(String[] args) {
		
		int y=20;
		int sum=0;
		System.out.println("sum of even numbers from 1 - 20 is:");
		for (int i=1; i<=y; i++) {
			if (i%2==0) {
			sum=sum+i;	
			}}
		System.out.println(sum);
		
		
		// TODO Auto-generated method stub

	}

}
