package PersonalPracticeAssignment2;

public class swapValue1 {

	public static void main(String[] args) {
			
		int x = 85;
		int y = 75;
		int temp;
		
		temp = x;
		x = y;
		y = temp;
		
		System.out.println("x is " +x+ " and y is " +y);
	
		x = 23;
		y = 34;
		
		x = x+y;
		y = x - y;
		x = x - y;
		
		System.out.println("x is " +x+ " and y is " +y);
		
		
		// TODO Auto-generated method stub

	}

}
